    def bat(self):
        if self.batradio.isChecked()==True:
            self.playerslist.clear()
            mycric=sqlite3.connect('cricket.db')
            curmycric=mycric.cursor()
            curmycric.execute("select player from stats where ctg='BAT'")
            while True:
                a=curmycric.fetchone()
                if a==None:
                    break
                else:
                    for b in a:
                        self.playerslist.addItem(str(b))
            mycric.close()
