    def addplayers(self, item):
        
        self.selectedplayerslist.addItem(QtWidgets.QListWidgetItem(item))
        n=self.playerslist.row(item)
        t=self.playerslist.takeItem(n).text()
        global tname
        curmycric=mycric.cursor()
        curmycric.execute("select value from stats where player=?",(t,))
        a=curmycric.fetchone()
        for b in a:
            v=int(b)
        curmycric.execute("INSERT INTO teams(name,players,value)VALUES(?,?,?)",(tname,t,v,))
        global tvalue
        global uvalue
        tvalue=tvalue-v
        uvalue=uvalue+v
        self.ptsavllabel.setText(str(tvalue))
        self.ptsusedlabel.setText(str(uvalue))
        curmycric.execute("select ctg from stats where player=?",(t,))
        c=curmycric.fetchone()
        global batcount
        global bwlcount
        global arcount
        global wkcount
        for s in c:
            c1=s
        if c1=="BAT":
            batcount=batcount+1
            self.batcountlabel.setText(str(batcount))
        if c1=="BWL":
            bwlcount=bwlcount+1
            self.bwlcountlabel.setText(str(bwlcount))
